mapper.select:
SELECT repair.*,CONCAT(floor.name,'-',unit,'-',house ) as roomName, customer.name AS customerName FROM repair LEFT JOIN room on room.id = repair.room_id LEFT JOIN floor on floor.id = room.floor_id LEFT JOIN customer on customer.id = room.customer_id where 1=1
and ( customer.name like CONCAT('%',#{keyword},'%') or room.name like CONCAT('%',#{keyword},'%') or repair.info like CONCAT('%',#{keyword},'%') )
and repair.room_id=#{room_id}
and repair.room_id in
#{item}
and repair.customer_id =#{customer_id}
ORDER BY repair.id desc
limit #{start},#{limit}

SELECT count(1) FROM repair LEFT JOIN room on room.id = repair.room_id LEFT JOIN customer on customer.id = room.customer_id where 1=1
and ( customer.name like CONCAT('%',#{keyword},'%') or room.name like CONCAT('%',#{keyword},'%') or repair.info like CONCAT('%',#{keyword},'%') )
and repair.room_id =#{room_id}
and repair.room_id in
#{item}
and repair.customer_id =#{customer_id}
